.block-quote {
  font-weight: 700;
  font-family: $font-family-serif;
  text-align: center;
  color: $text-primary;

  > span {
    margin: 0 auto;
    padding: spacing(1, 2);
  }
}

////// Footer //////
.footer {
  margin-bottom: 0;

  @include up('sm') {
    .contact {
      display: flex;
      justify-content: space-between;
    }
  }
}

.footer-text {
  margin: spacing(2, 0);
  line-height: 1.65;
  font-size: 0.85rem;

  span {
    display: block;
  }
}

.subscribe {
  margin: spacing(2, 0);
  text-decoration: underline;
}

.social {
  margin: spacing(1, 0);

  > span {
    font-weight: 700;
  }

  &__icons {
    margin: spacing(1, 0);
  }

  &__icons span {
    width: 40px;
    height: 40px;

    display: inline-block;

    border: 2px solid $background-dark;
    border-radius: 100%;

    font-size: 0.75rem;
    text-align: center;
    line-height: 36px; // minus border width
    text-transform: uppercase;

    cursor: pointer;

    &:hover {
      background-color: $background-dark;
      color: $white;
      transition: background-color, color 400ms ease-out;
    }
  }
}

.copyright {
  margin-top: spacing(2);
  font-size: 0.65em;
  opacity: 0.5;
}

/* ==========================================================================
 * States
 * ========================================================================== */
/* Display */
@include up('sm') {
  .is-displayed-mobile {
    display: none;
  }
}

.is-hidden-mobile {
  display: none;
}
@include up('sm') {
  .is-hidden-mobile {
    display: block;
  }
}

